import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import NavigationLogout from '../NavigationLogout';
import './EditTask.css';

const EditTask = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [taskForm, setTaskForm] = useState({
    title: '',
    description: '',
    date: '',
    priority: 'LOW',
    status: 'Pending',
    reminderEnabled: false,
    reminderDays: 1,
    reminderTime: '09:00'
  });

  useEffect(() => {
    fetchTask();
  }, [id]);

  const fetchTask = async () => {
    try {
      const token = localStorage.getItem('jwt');
      const res = await axios.get(`http://localhost:8080/api/tasks/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const task = res.data;
      setTaskForm({
        title: task.title || '',
        description: task.description || '',
        date: task.date || '',
        priority: task.priority || 'LOW',
        status: task.status || 'Pending',
        reminderEnabled: task.reminderEnabled || false,
        reminderDays: task.reminderDays || 1,
        reminderTime: task.reminderTime || '09:00'
      });
    } catch (error) {
      console.error('Failed to fetch task:', error);
      alert('Failed to load task data');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setTaskForm({
      ...taskForm,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('jwt');
      await axios.put(`http://localhost:8080/api/tasks/${id}`, taskForm, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Task updated successfully!');
      navigate('/userHome');
    } catch (error) {
      console.error('Failed to update task:', error);
      alert('Failed to update task. Please try again.');
    }
  };

  if (loading) {
    return (
      <>
        <NavigationLogout />
        <div className="edit-task-container">
          <h2>Loading...</h2>
        </div>
      </>
    );
  }

  return (
    <>
      <NavigationLogout />
      <div className="edit-task-container">
        <h2>Edit Task</h2>
        
      </div>
    </>
  );
};

export default EditTask;
