import React from 'react';
import NavigationBar from './NavigationBar';
import './AboutUs.css';

const AboutUs = () => {
  return (
    <>
      <NavigationBar />

      {/* Move hero OUTSIDE container to allow full-width */}
      <section className="aboutus-hero">
        <h1>About <span class>Advanced To-Do</span></h1>
        <p>Redefining task management with innovation, precision, and elegance.</p>
      </section>

      <div className="aboutus-container">
        {/* Mission Statement */}
        <section className="aboutus-mission">
          <div className="aboutus-mission-text">
            <h2>Who We Are</h2>
            <p>
              Founded in 2023, we aim to revolutionize productivity by delivering intuitive, smart, and scalable task management solutions. Whether you're an individual or part of a large team, our tools are designed to fit your lifestyle and workflow.
            </p>
          </div>
          <div className="aboutus-mission-image">
            <img
              src="https://media.istockphoto.com/id/1284038459/photo/im-sure-ill-have-all-the-boxes-checked-by-tonight.jpg?s=612x612&w=0&k=20&c=Ax0lmBiU67ZdkmDhlT5-NIgBtUCmtQngac9E7fFRWQo="
              alt="About Us"
            />
          </div>
        </section>

        {/* Why Choose Us */}
        <section className="aboutus-why">
          <h2 className="section-title-why">Why Choose Us?</h2>
          <p>Fast, intuitive, and powerful — here's how we help you stay productive</p>
        </section>

        {/* Capture Tasks Quickly */}
        <section className="aboutus-capture">
          <div className="capture-grid">
            {[
              {
                title: "Third-Party App Add",
                desc: "Quickly add tasks via Weibo, email plugin, or browser text selection.",
                img: "https://d107mjio2rjf74.cloudfront.net/sites/res/newFeatures/tick/feature1/more.png"
              },
              {
                title: "Widget Add",
                desc: "Add tasks from your home screen and view them instantly.",
                img: "https://d107mjio2rjf74.cloudfront.net/sites/res/newFeatures/tick/feature1/widget.png"
              },
              {
                title: "Desktop Shortcut Add",
                desc: "Use a global shortcut to instantly add tasks from anywhere.",
                img: "https://d107mjio2rjf74.cloudfront.net/sites/res/newFeatures/tick/feature1/quickAdd.png"
              },
              {
                title: "Google Calendar Add",
                desc: "Seamlessly integrate with Google Calendar for productivity sync.",
                img: "https://d107mjio2rjf74.cloudfront.net/sites/res/newFeatures/tick/feature1/google.png"
              },
              {
                title: "NLP",
                desc: "Use natural language to set task times quickly and intuitively.",
                img: "https://d107mjio2rjf74.cloudfront.net/sites/res/newFeatures/tick/feature1/nlp.png"
              },
              {
                title: "Voice Add",
                desc: "Speak your tasks aloud and they’ll be instantly captured.",
                img: "https://d107mjio2rjf74.cloudfront.net/sites/res/newFeatures/tick/feature1/voice.png"
              }
            ].map((item, index) => (
              <div key={index} className={`capture-card floating delay-${index}`}>
                <img src={item.img} alt={item.title} />
                <h3>{item.title}</h3>
                <p>{item.desc}</p>
              </div>
            ))}
          </div>
        </section>
      </div>
    </>
  );
};

export default AboutUs;
