import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Task.css';

const Task = () => {
  const [users, setUsers] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [formData, setFormData] = useState({
    userId: '',
    title: '',
    description: '',
    date: '',
    priority: 'LOW',
    status: 'Pending'
  });

  // Fetch users for dropdown
  useEffect(() => {
    fetchUsers();
    fetchTasks();
  }, []);

  const fetchUsers = async () => {
    try {
      const res = await axios.get('http://localhost:8080/api/users');
      setUsers(res.data);
    } catch (error) {
      console.error('Failed to fetch users:', error);
    }
  };

  const fetchTasks = async () => {
    try {
      const res = await axios.get('http://localhost:8080/api/tasks');
      setTasks(res.data);
    } catch (error) {
      console.error('Failed to fetch tasks:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const selectedUser = users.find(u => u.id === parseInt(formData.userId));
      const payload = { ...formData, user: selectedUser };
      await axios.post('http://localhost:8080/api/tasks', payload);
      alert('Task assigned successfully!');
      setFormData({
        userId: '',
        title: '',
        description: '',
        date: '',
        priority: 'LOW',
        status: 'Pending'
      });
      fetchTasks();
    } catch (error) {
      console.error('Failed to create task:', error);
    }
  };

  return (
    <div className="task-container">
      <h2>Assign Task</h2>
      <form className="task-form" onSubmit={handleSubmit}>
        <label>User</label>
        <select name="userId" value={formData.userId} onChange={handleChange} required>
          <option value="">Select User</option>
          {users.map(user => (
            <option key={user.id} value={user.id}>{user.name}</option>
          ))}
        </select>

        <label>Title</label>
        <input
          type="text"
          name="title"
          value={formData.title}
          onChange={handleChange}
          placeholder="Enter task title"
          required
        />

        <label>Description</label>
        <textarea
          name="description"
          value={formData.description}
          onChange={handleChange}
          placeholder="Enter task description"
          rows="4"
          required
        />

        <label>Date</label>
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          required
        />

        <label>Priority</label>
        <select name="priority" value={formData.priority} onChange={handleChange} required>
          <option value="LOW">LOW</option>
          <option value="MEDIUM">MEDIUM</option>
          <option value="HIGH">HIGH</option>
        </select>

        <label>Status</label>
        <select name="status" value={formData.status} onChange={handleChange} required>
          <option value="Pending">Pending</option>
          <option value="In Progress">In Progress</option>
          <option value="Completed">Completed</option>
        </select>

        <button type="submit" className="submit-btn">Assign Task</button>
      </form>

      <h3>Task List</h3>
      <table className="task-table">
        <thead>
          <tr>
            <th>User</th>
            <th>Title</th>
            <th>Description</th>
            <th>Date</th>
            <th>Priority</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {tasks.length === 0 ? (
            <tr><td colSpan="6">No tasks assigned yet</td></tr>
          ) : (
            tasks.map(task => (
              <tr key={task.id}>
                <td>{task.user.name}</td>
                <td>{task.title}</td>
                <td>{task.description}</td>
                <td>{task.date}</td>
                <td>{task.priority}</td>
                <td>{task.status}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Task;
