import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import NotificationsActiveIcon from '@mui/icons-material/NotificationsActive';
import './NavigationLogout.css'; // Assuming you're styling it separately

const NavigationLogout = () => {
  const navigate = useNavigate();
  const [showDropdown, setShowDropdown] = useState(false);
  const [hasNotifications, setHasNotifications] = useState(false);
  const currentUser = JSON.parse(localStorage.getItem('currentUser') || '{}');
  const userRole = currentUser.role;

  useEffect(() => {
    const checkForReminders = () => {
      const reminderSection = document.querySelector('.reminders-section');
      setHasNotifications(reminderSection && reminderSection.children.length > 1);
    };
    
    const interval = setInterval(checkForReminders, 1000);
    return () => clearInterval(interval);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("jwt");
    localStorage.removeItem("role");
    localStorage.removeItem("currentUser");
    navigate("/");
  };

  const handleHome = () => {
    navigate("/");
  };

  const handleFeatures = () => {
    navigate("/featureSection");
  };

  const handleAboutUs = () => {
    navigate("/aboutUs");
  };

  const handleDashboard = () => {
    if (userRole === 'ADMIN') {
      navigate('/adminPage');
    } else if (userRole === 'USER') {
      navigate('/userHome');
    } else if (userRole === 'MANAGER') {
      navigate('/manager');
    }
    setShowDropdown(false);
  };


  return (
    <div className="nav">
      <nav className="navbar">
        {/* Left links */}
        <ul className="nav-left">
          <div className="logo">AdvancedToDo</div>
          <li onClick={handleHome}>Home</li>
          <li onClick={handleFeatures}>Features</li>
          <li onClick={handleAboutUs}>About Us</li>
        </ul>

        {/* Right buttons */}
        <div className="nav-right">
          <button className="logout-btn" onClick={handleLogout}>
            LOGOUT
          </button>
          <NotificationsActiveIcon 
            className={`notification-icon ${hasNotifications ? 'shake' : ''}`}
          />
          <div className="profile-dropdown">
            <AccountCircleIcon 
              className="profile-icon" 
              onClick={() => setShowDropdown(!showDropdown)}
            />
            {showDropdown && (
              <div className="dropdown-menu">
                <div className="dropdown-item" onClick={handleDashboard}>
                  {userRole ? `${userRole.charAt(0) + userRole.slice(1).toLowerCase()} Dashboard` : 'Dashboard'}
                </div>
              </div>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
};

export default NavigationLogout;
