import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ProfilePage.css';
import NavigationLogout from '../NavigationLogout';

const ProfilePage = () => {
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const fetchUser = async () => {
      const currentUser = JSON.parse(localStorage.getItem('currentUser') || '{}');
      if (!currentUser.id) return;

      try {
        const token = localStorage.getItem('jwt');
        const res = await axios.get(`http://localhost:8080/api/users/${currentUser.id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUser(res.data);
      } catch (err) {
        console.error("Error fetching user:", err);
      }
    };

    fetchUser();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser(prev => ({ ...prev, [name]: value }));
  };

  const handleEditToggle = () => {
    if (isEditing) {
      // Save on backend
      const token = localStorage.getItem('jwt');
      axios.put(`http://localhost:8080/api/users/${user.id}`, user, {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(() => alert("Profile updated"))
        .catch(() => alert("Failed to update"));
    }
    setIsEditing(!isEditing);
  };

  const handleProfileClick = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'image/*';
    input.onchange = (e) => {
      const file = e.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = (event) => {
          const imageData = event.target.result;
          setUser(prev => ({ ...prev, profileImage: imageData }));
          localStorage.setItem('profileImage_' + user.id, imageData);
        };
        reader.readAsDataURL(file);
      }
    };
    input.click();
  };

  if (!user) return <div>Loading...</div>;

  return (
    <>
    <NavigationLogout/>
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-info">
          <img 
            src={localStorage.getItem('profileImage_' + user.id) || user.profileImage || "https://i.pravatar.cc/150?img=11"} 
            alt="avatar" 
            className="profile-pic clickable" 
            onClick={handleProfileClick}
            title="Click to change profile picture"
          />
          <div className="user-info">
            <h3>{user.name}</h3>
            <p>{user.email}</p>
          </div>
          <button className="edit-button" onClick={handleEditToggle}>
            {isEditing ? 'Save' : 'Edit'}
          </button>
        </div>

        <div className="form-section">
          <div className="form-group">
            <label>Full Name</label>
            <input type="text" name="name" value={user.name} onChange={handleChange} disabled={!isEditing} />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input type="text" name="password" value={user.password} onChange={handleChange} disabled={!isEditing} />
          </div>
          <div className="form-group">
            <label>Role</label>
            <input type="text" name="role" value={user.role} onChange={handleChange} disabled />
          </div>
        </div>

        <div className="email-section">
          <h4>My email Address</h4>
          <div className="email-box">
            <span className="email-icon">ðŸ“§</span>
            <div>
              <p>{user.email}</p>
              <small>Member since unknown</small>
            </div>
          </div>
        </div>
      </div>
    </div>
    </>
  );
};

export default ProfilePage;
