import React, { useEffect, useState } from 'react';
import './UserList.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const UserList = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [newUserId, setNewUserId] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const res = await axios.get('http://localhost:8080/api/users');
      setUsers(res.data);

      // Assuming the last user added is new
      if (res.data.length > 0) {
        const lastUser = res.data[res.data.length - 1];
        setNewUserId(lastUser.id); // Requires 'id' field in user
      }

    } catch (error) {
      console.error('Failed to fetch users:', error);
    }
  };

  const handleToNewUsers = () => {
    navigate('/addusers');
  };

  const handleEdit = (id) => {
    alert(`Edit user with ID: ${id}`);
    // navigate(`/edituser/${id}`) -- You can later implement this route
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await axios.delete(`http://localhost:8080/api/users/${id}`);
        fetchUsers(); // Refresh list
      } catch (error) {
        console.error('Delete failed:', error);
      }
    }
  };

  return (
    <div className="user-list-container">
      <div className="header-row">
        <h2>Users</h2>
        <button className="add-button" onClick={handleToNewUsers}>➕ ADD USERS</button>
        <span className="total-count">Total Users: {users.length}</span>
      </div>

      <input type="text" placeholder="Search" className="search-box" />

      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>

      </table>

      <div className="pagination">
        <span>Rows per page:</span>
        <select>
          <option>5</option>
          <option>10</option>
          <option>25</option>
        </select>
        <span>1–{users.length} of {users.length}</span>
        <button>{'<'}</button>
        <button>{'>'}</button>
      </div>
    </div>
  );
};

export default UserList;
