import React from 'react';
import { Box, Typography, Button, Container, Grid, Card, CardContent, useMediaQuery } from '@mui/material';
import { useTheme } from '@mui/material/styles';
import { Link } from 'react-router-dom';
import './LandingPage.css'; // import the CSS file

const LandingPage = () => {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <Box className="landing-page">
      <Container maxWidth="lg">
        {/* Welcome Box */}
        <Box className="welcome-box">
          <Typography
            variant="h3"
            className={`welcome-title ${isSmallScreen ? 'small' : ''}`}
          >
            Welcome to Employee Management System
          </Typography>
          <Typography
            variant="h6"
            className={`welcome-subtitle ${isSmallScreen ? 'small' : ''}`}
          >
            A centralized solution for managing all your employee data efficiently and effectively.
          </Typography>
          <Button
            component={Link}
            to="/dashboard"
            className="welcome-button"
            variant="contained"
          >
            Go to Dashboard
          </Button>
        </Box>

        {/* Features Section */}
        <Grid container spacing={4} className="features-grid">
          {/* Feature Cards */}
          {[
            {
              title: 'Manage Employees',
              description: 'Easily manage your employee records, track details, and maintain an up-to-date roster.',
              link: '/employees',
              buttonText: 'View Employees',
            },
            {
              title: 'Track Departments',
              description: 'Organize your company’s structure with detailed information on each department.',
              link: '/departments',
              buttonText: 'View Departments',
            },
            {
              title: 'Analyze Growth',
              description: 'Use our comprehensive dashboard to analyze employee growth and organizational structure.',
              link: '/dashboard',
              buttonText: 'Go to Dashboard',
            },
          ].map((feature, index) => (
            <Grid item xs={12} md={4} key={index}>
              <Card className="feature-card">
                <CardContent>
                  <Typography variant="h5" className="feature-title">{feature.title}</Typography>
                  <Typography variant="body1" className="feature-desc">{feature.description}</Typography>
                  <Button
                    component={Link}
                    to={feature.link}
                    className="feature-button"
                    variant="contained"
                  >
                    {feature.buttonText}
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>

        {/* Additional Info Sections */}
        <Box className="info-box">
          <Typography variant="h5" className="info-title">Why Choose Our System?</Typography>
          <Typography variant="body1" className="info-desc">
            Our Employee Management System is designed to simplify HR processes, streamline department management, and provide valuable insights into your workforce.
          </Typography>
        </Box>

        <Box className="info-box">
          <Typography variant="h5" className="info-title">Learn More</Typography>
          <Typography variant="body1" className="info-desc">
            Visit our documentation to learn more about the features, functionalities, and how to get started.
          </Typography>
          <Button
            href="https://github.com/hoangsonww/Employee-Management-Fullstack-App"
            target="_blank"
            className="feature-button"
            variant="contained"
          >
            Documentation
          </Button>
        </Box>
      </Container>
    </Box>
  );
};

export default LandingPage;
