import React, { useState } from 'react';
import { TextField, Button, Card, CardContent, Typography, Box, CircularProgress, IconButton, InputAdornment } from '@mui/material';
import { Visibility, VisibilityOff } from '@mui/icons-material';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch('https://employee-management-app-gdm5.onrender.com/authenticate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();
      setLoading(false);

      if (response.ok) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('EMSusername', username);
        alert('Login successful. Welcome!');
      } else {
        setError('Invalid credentials. Please try again.');
      }
    } catch (err) {
      setLoading(false);
      setError('Invalid credentials or server not active. Please try later.');
    }
  };

  const handleTogglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        px: 2, // padding for mobile
      }}
    >
      <Card
        sx={{
          width: { xs: '100%', sm: '80%', md: '50%', lg: '40%' }, // responsive width
          boxShadow: 3,
          borderRadius: 4,
          p: { xs: 2, sm: 3, md: 4 }, // responsive padding
          backgroundColor: '#fff',
        }}
      >
        <CardContent>
          <Typography
            variant="h5"
            component="h2"
            textAlign="center"
            sx={{ mb: { xs: 2, sm: 3 } }}
          >
            Login
          </Typography>
          <form onSubmit={handleSubmit}>
            <TextField
              fullWidth
              label="Username"
              value={username}
              onChange={e => setUsername(e.target.value)}
              sx={{ mb: { xs: 2, sm: 3 } }}
              InputProps={{
                style: { fontFamily: 'Poppins, sans-serif' },
              }}
            />
            <TextField
              fullWidth
              label="Password"
              type={showPassword ? 'text' : 'password'}
              value={password}
              onChange={e => setPassword(e.target.value)}
              sx={{ mb: { xs: 2, sm: 3 } }}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton
                      aria-label="toggle password visibility"
                      onClick={handleTogglePasswordVisibility}
                      edge="end"
                    >
                      {showPassword ? <VisibilityOff /> : <Visibility />}
                    </IconButton>
                  </InputAdornment>
                ),
                style: { fontFamily: 'Poppins, sans-serif' },
              }}
            />
            {loading ? (
              <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
                <CircularProgress />
              </Box>
            ) : (
              <Button fullWidth variant="contained" color="primary" type="submit" sx={{ mb: 2 }}>
                Login
              </Button>
            )}
            {error && (
              <Typography color="error" textAlign="center" sx={{ mb: 2 }}>
                {error}
              </Typography>
            )}
            <Typography textAlign="center" sx={{ mb: 1 }}>
              Don't have an account?{' '}
              <Button color="primary" component="a" href="/register">
                Register
              </Button>
            </Typography>
            <Typography textAlign="center">
              Forgot your password?{' '}
              <Button color="primary" component="a" href="/verify-username">
                Reset Password
              </Button>
            </Typography>
          </form>
        </CardContent>
      </Card>
    </Box>
  );
};

export default Login;
