import React, { useState } from "react";
import "./Login.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    email: "",        // ✅ changed from 'username'
    password: "",
    role: "",
  });

  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // ✅ Call login endpoint with correct fields
      const res = await axios.post("http://localhost:8080/api/users/login", formData);

      alert("Login successful!");
      if(res.status!==200)
      {
        console.log("error----------------------------->>>");
      }
      // ✅ Navigate based on role (backend expects: ADMIN, USER, MANAGER)
      const role = formData.role.toUpperCase();
      if (role === "ADMIN") {
        navigate("/admin");
      } else if (role === "USER") {
        navigate("/user");
      } else if (role === "MANAGER") {
        navigate("/manager");
      }
    } catch (error) {
      console.error("Login failed:", error.response?.data || error.message);
      alert("Invalid credentials. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          {/* Email Input */}
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          {/* Password Input */}
          <div className="form-group password-container">
            <label>Password</label>
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              placeholder="Enter your password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>

          {/* Role Dropdown */}
          <div className="form-group">
            <label>Select Role</label>
            <select
              name="role"
              value={formData.role}
              onChange={handleChange}
              required
            >
              <option value="" disabled>
                Select your role
              </option>
              <option value="ADMIN">Admin</option>
              <option value="USER">User</option>
              <option value="MANAGER">Manager</option>
            </select>
          </div>

          {/* Login Button */}
          <button type="submit" className="login-btn">
            LOGIN
          </button>
        </form>

        {/* Navigation Links */}
        <p className="register-link">
          Don’t have an account? <a href="/register">REGISTER</a>
        </p>
        <p className="forgot-password">Forgot your password?</p>
      </div>
    </div>
  );
};

export default Login;
