import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserHome.css';

const UserHome = () => {
  const [tasks, setTasks] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [showTaskPopup, setShowTaskPopup] = useState(false);
  const [activeView, setActiveView] = useState('inbox');
  const [searchTerm, setSearchTerm] = useState('');
  const [taskForm, setTaskForm] = useState({
    title: '',
    description: '',
    date: '',
    priority: 'LOW',
    status: 'Pending'
  });

  useEffect(() => {
    fetchTasks();
    getCurrentUser();
  }, []);

  const getCurrentUser = () => {
    const user = JSON.parse(localStorage.getItem('currentUser') || '{}');
    setCurrentUser(user);
  };

  const fetchTasks = async () => {
    try {
      const res = await axios.get('http://localhost:8080/api/tasks');
      const currentUserId = JSON.parse(localStorage.getItem('currentUser') || '{}').id;
      const userTasks = res.data.filter(task => task.user.id === currentUserId);
      setTasks(userTasks);
    } catch (error) {
      console.error('Failed to fetch tasks:', error);
    }
  };

  const handleTaskSubmit = async (e) => {
    e.preventDefault();
    try {
      const taskData = {
        title: taskForm.title,
        description: taskForm.description,
        date: taskForm.date,
        priority: taskForm.priority,
        status: taskForm.status,
        user: { id: currentUser.id }
      };
      
      await axios.post('http://localhost:8080/api/tasks', taskData);
      
      alert('Task added successfully!');
      setShowTaskPopup(false);
      setTaskForm({ title: '', description: '', date: '', priority: 'LOW', status: 'Pending' });
      fetchTasks();
    } catch (error) {
      console.error('Failed to create task:', error);
      alert('Failed to add task. Please try again.');
    }
  };

  const handleTaskComplete = async (taskId) => {
    try {
      const task = tasks.find(t => t.id === taskId);
      await axios.put(`http://localhost:8080/api/tasks/${taskId}`, {
        ...task,
        status: 'Completed'
      });
      fetchTasks();
    } catch (error) {
      console.error('Failed to update task:', error);
    }
  };

  const getFilteredTasks = () => {
    const today = new Date().toISOString().split('T')[0];
    const filtered = tasks.filter(task => {
      if (searchTerm && !task.title.toLowerCase().includes(searchTerm.toLowerCase())) {
        return false;
      }
      switch (activeView) {
        case 'today': return task.date === today;
        case 'upcoming': return new Date(task.date) > new Date();
        case 'completed': return task.status === 'Completed';
        default: return task.status !== 'Completed';
      }
    });
    return filtered;
  };

  const getTaskCounts = () => {
    const today = new Date().toISOString().split('T')[0];
    return {
      inbox: tasks.filter(t => t.status !== 'Completed').length,
      today: tasks.filter(t => t.date === today).length,
      upcoming: tasks.filter(t => new Date(t.date) > new Date()).length,
      completed: tasks.filter(t => t.status === 'Completed').length
    };
  };

  const counts = getTaskCounts();
  const filteredTasks = getFilteredTasks();

  return (
    <div className="user-home">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="profile">
          <span className="avatar">{currentUser?.name?.charAt(0) || 'U'}</span>
          <span className="username">{currentUser?.name || 'User'} ‚ñæ</span>
        </div>
        <button className="add-task-btn" onClick={() => setShowTaskPopup(true)}>+ Add task</button>
        <input 
          type="text" 
          className="search" 
          placeholder="Search" 
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />

        <nav className="menu">
          <div 
            className={`menu-item ${activeView === 'inbox' ? 'selected' : ''}`}
            onClick={() => setActiveView('inbox')}
          >
            üì• Inbox <span className="count">{counts.inbox}</span>
          </div>
          <div 
            className={`menu-item ${activeView === 'today' ? 'selected' : ''}`}
            onClick={() => setActiveView('today')}
          >
            üìÖ Today <span className="count">{counts.today}</span>
          </div>
          <div 
            className={`menu-item ${activeView === 'upcoming' ? 'selected' : ''}`}
            onClick={() => setActiveView('upcoming')}
          >
            üîú Upcoming <span className="count">{counts.upcoming}</span>
          </div>
          <div className="menu-item">üè∑Ô∏è Filters & Labels</div>
          <div 
            className={`menu-item ${activeView === 'completed' ? 'selected' : ''}`}
            onClick={() => setActiveView('completed')}
          >
            ‚úÖ Completed <span className="count">{counts.completed}</span>
          </div>
        </nav>

        <div className="projects">
          <h4>My Projects</h4>
          <div className="menu-item">üè° Personal <span className="count">{tasks.filter(t => t.priority === 'LOW').length}</span></div>
          <div className="menu-item">üéØ Work <span className="count">{tasks.filter(t => t.priority === 'HIGH').length}</span></div>
          <div className="menu-item">üìö Learning <span className="count">{tasks.filter(t => t.priority === 'MEDIUM').length}</span></div>
        </div>

        <div className="footer">
          <div className="menu-item">+ Add a team</div>
          <div className="menu-item">‚ùì Help & resources</div>
        </div>
      </aside>

      {/* Main content */}
      <main className="main-content">
        <div className="inbox-header">
          {activeView === 'inbox' && 'Inbox'}
          {activeView === 'today' && 'Today'}
          {activeView === 'upcoming' && 'Upcoming'}
          {activeView === 'completed' && 'Completed'}
        </div>
        
        {activeView === 'upcoming' ? (
          <div className="calendar-view">
            {filteredTasks.map(task => (
              <div key={task.id} className="calendar-task">
                <div className="task-date">{new Date(task.date).toLocaleDateString()}</div>
                <div className="task-info">
                  <div className="task-title">{task.title}</div>
                  <div className={`task-priority priority-${task.priority.toLowerCase()}`}>{task.priority}</div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <ul className="task-list">
            {filteredTasks.length === 0 ? (
              <li className="no-tasks">No tasks found</li>
            ) : (
              filteredTasks.map(task => (
                <li key={task.id} className="task-item">
                  <input 
                    type="checkbox" 
                    checked={task.status === 'Completed'}
                    onChange={() => handleTaskComplete(task.id)}
                  />
                  <div>
                    <div className="task-title">{task.title}</div>
                    <div className="task-subtext">
                      {task.priority === 'HIGH' && 'üî¥'}
                      {task.priority === 'MEDIUM' && 'üü†'}
                      {task.priority === 'LOW' && 'üü¢'}
                      {' '}{new Date(task.date).toLocaleDateString()}
                    </div>
                  </div>
                </li>
              ))
            )}
          </ul>
        )}
        
        <button className="add-task-inline" onClick={() => setShowTaskPopup(true)}>+ Add task</button>
      </main>

      {/* Task Popup */}
      {showTaskPopup && (
        <div className="popup-overlay">
          <div className="popup">
            <h3>Add New Task</h3>
            <form onSubmit={handleTaskSubmit}>
              <input
                type="text"
                placeholder="Task title"
                value={taskForm.title}
                onChange={(e) => setTaskForm({...taskForm, title: e.target.value})}
                required
              />
              <textarea
                placeholder="Description"
                value={taskForm.description}
                onChange={(e) => setTaskForm({...taskForm, description: e.target.value})}
                required
              />
              <input
                type="date"
                value={taskForm.date}
                onChange={(e) => setTaskForm({...taskForm, date: e.target.value})}
                required
              />
              <select
                value={taskForm.priority}
                onChange={(e) => setTaskForm({...taskForm, priority: e.target.value})}
              >
                <option value="LOW">Low Priority</option>
                <option value="MEDIUM">Medium Priority</option>
                <option value="HIGH">High Priority</option>
              </select>
              <div className="popup-buttons">
                <button type="submit">Add Task</button>
                <button type="button" onClick={() => setShowTaskPopup(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserHome;
